mapper.select:
SELECT id, type, name, sort, status
FROM category
WHERE name = #{name}

mapper.update:
UPDATE category
SET status      = #{status},
    update_time = #{time},
    update_user = #{updateUser}
WHERE id = #{id}

mapper.select:
SELECT id, type, name, sort, status, update_time
FROM category
WHERE id = #{id}

SELECT id, type, name, sort, status, update_time FROM category
type = #{type}
ORDER BY type, sort, update_time DESC

