mapper.select:
select id, number, status, user_id, address_book_id, order_time, checkout_time, pay_method, pay_status, amount, remark, phone, address, user_name, consignee, cancel_reason, rejection_reason, cancel_time, estimated_delivery_time, delivery_status, delivery_time, pack_amount, tableware_number, tableware_status from orders
and user_id = #{condition.userId}
and status = #{condition.status}
and number = #{condition.number}
and phone = #{condition.phone}
and order_time >= #{condition.beginTime}
and order_time <= #{condition.endTime}
order by status, order_time desc

select sum(amount)
from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select count(id)
from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select od.name as name, sum(od.number) as number
from order_detail od,
     orders o
where od.order_id = o.id
  and o.status = 5

    and order_time >= #{begin}


    and order_time <= #{end}

group by name
order by number desc
limit 0, 10
